exports.setFreq = function(freq, pin) {
    if (pin) {
        console.log('Setting frequency of ' + freq + 'Hz for pin ' + pin + '.');
    } else {
        console.log('Setting frequency of ' + freq + 'Hz for all pins.');
    }
};

exports.setPWM = function(pin, duty) {
    console.log('Setting duty cycle to ' + duty + ' of 4095 on pin ' + pin + '.');
};